const orderInputs=document.querySelectorAll(".orderForm-input"),submitOrder=document.querySelector(".order__btn"),timeInputs=document.querySelectorAll(".time-block__timepicker");let inputsObj=JSON.parse(localStorage.getItem("orderForm"))||{};function getSelectedTime(){timeInputs.forEach(e=>{e.value&&(inputsObj={...inputsObj,[e.name]:e.value})})}orderInputs.forEach(e=>{e.addEventListener("change",()=>{e.value&&(inputsObj=Array.from(orderInputs).filter(e=>""!==e.value).reduce((e,t)=>({...e,[t.name]:t.value}),{}))})}),submitOrder.addEventListener("click",()=>{getSelectedTime(),localStorage.setItem("orderForm",JSON.stringify(inputsObj)),console.log("submit")});